mapper.update:
UPDATE TB_PRO_MEM
SET
JBTTL_CD = #{jbttlCd}
WHERE
STU_NO = #{stuNo} AND PROJ_NO = #{projNo}

mapper.delete:
DELETE
FROM TB_JOIN_APLY
WHERE STU_NO = #{stuNo} AND PROJ_NO = #{projNo}

mapper.update:
UPDATE TB_PROJ
SET PROJ_TNOPE = (
    SELECT COUNT(STU_NO)
    FROM TB_PRO_MEM
    WHERE PROJ_NO = #{projNo}
)
WHERE PROJ_NO = #{projNo}

mapper.select:
SELECT JBTTL_CD
FROM TB_PRO_MEM
WHERE STU_NO = #{userName}

mapper.update:
UPDATE TB_PROJ
SET
PROJ_NM = #{projNm}
, PROJ_EXPLN = #{projExpln}
, PROJ_STTS_CD = #{projSttsCd}
, PROJ_TECH = #{projTech}
, PROJ_STTR = #{projSttr}
, PROJ_BGNG_DT = #{projBgngDt}
, PROJ_DDLN_DT = #{projDdlnDt}
WHERE
PROJ_NO = #{projNo}
