mapper.update:
UPDATE TB_STU
SET
	USER_EML_ADDR = #{userEmlAddr}
	, USER_ZIP = #{userZip}
	, USER_ADDR = #{userAddr}
	, USER_DADDR = #{userDaddr}
	, JOIN_DT = sysdate
	, USER_PRVC_YN = #{userPrvcYn}
	, USER_GENDER = #{userGender}
	, USER_PROFILE = #{userProfile}
WHERE STU_NO = #{userNo}

UPDATE TB_USER
SET
	USER_PSWD = #{userPswd}
WHERE USER_NO = #{userNo}

mapper.insert:
/* team.tttt.pms.common.user.mapper.UserMapper.insertSms(SmsVO) */
INSERT INTO TB_SMS (
	SMS_NO
	, SMS_CN
	, SMS_RCVR
	)
VALUES (
	SEQ_SMS.NEXTVAL
	, #{smsCn}
	, #{smsRcvr}
	)

mapper.select:
	SELECT
		STU_NO AS USER_NO
	FROM TB_STU
	WHERE
		USER_NM = #{userNm}
		AND USER_EML_ADDR = #{userEmlAddr}
		AND USER_BRDT = #{userBrdt}
UNION
	SELECT
		EMP_NO AS USER_NO
	FROM TB_EMP
	WHERE
		USER_NM = #{userNm}
		AND USER_EML_ADDR = #{userEmlAddr}
		AND USER_BRDT = #{userBrdt}

	SELECT
		USER_NM AS USER_NM
		, USER_PHONENO AS USER_PHONENO
	FROM TB_STU
	WHERE
		STU_NO = #{userNo}
		AND USER_PHONENO = #{userPhoneno}
		AND USER_BRDT = #{userBrdt}
UNION
	SELECT
		USER_NM AS USER_NM
		, USER_PHONENO AS USER_PHONENO
	FROM TB_EMP
	WHERE
		EMP_NO = #{userNo}
		AND USER_PHONENO = #{userPhoneno}
		AND USER_BRDT = #{userBrdt}

mapper.update:
UPDATE TB_USER
SET	USER_PSWD = #{tempPw}
WHERE USER_NO = #{userNo}
