mapper.select:
select b.* from( select a.* , DENSE_RANK() OVER (ORDER BY a.Rnumber DESC) AS rnum from( select a.STR_NO , a.STR_TITLE , a.STR_CN , a.STU_no , TO_char(a.STR_REG_DT, 'YYYY-MM-DD') as STR_REG_DT , a.ATCH_FILE_NO , a.PROJ_NO , b.user_nm , TO_NUMBER(SUBSTR(STR_NO, 3)) as Rnumber , str_views , b.FILE_THUMB , b.FILE_NM from tb_storage a join tb_stu b on a.Stu_NO = b.Stu_NO LEFT JOIN tb_atch_file b ON a.ATCH_FILE_NO = b.ATCH_FILE_NO where proj_no = #{projNo}
order by RNumber desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:
insert into tb_storage(
    STR_NO
    , STR_TITLE
    , STR_CN
    , STU_no
    , STR_reg_DT
    , ATCH_FILE_NO
    , PROJ_NO
) values(
    'st' || seq_storage.nextval
    , #{strTitle}
    , #{strCn}
    , #{stuNo}
    , sysdate
    , 'fileNo'|| seq_fileNo.nextval
    , #{projNo}
)

mapper.update:
update
    tb_storage
set
    STR_TITLE = #{strTitle}
    , STR_CN = #{strCn}
where
    STR_NO = #{strNo}
