mapper.insert:
INSERT INTO TB_QSTN(
      SBJCT_CD
    , QSTN_NO
    , QSTN_CN
    , QSTN_DT
    , STU_NO
    , QSTN_TTL)
VALUES (
      #{sbjctCd}
    , SEQ_TB_QSTN.NEXTVAL
    , #{qstnCn}
    , sysdate
    , #{stuNo}
    , #{qstnTtl}
)

mapper.update:
UPDATE
	TB_QSTN
SET
	QSTN_TTL = #{qstnTtl}
	, QSTN_CN = #{qstnCn}
WHERE
	QSTN_NO = #{qstnNo}
